import React, { Component } from "react";
import "./Login.css";
import { Link } from "react-router-dom";
import { getEmail } from '../actions'

import { connect } from 'react-redux'

class Login extends Component {
  render() {
    return (
      <div className="background-login">
        <div className="login">
          <fieldset>
            <legend>Preencha os dados para Login</legend>
            <legend className="datas-to-login">Email</legend>
            <input onChange={({target}) => this.props.get_email(target.value)} placeholder="Digite aqui seu email" type="text" />
            <legend className="datas-to-login">Senha</legend>
            <input onChange={({target}) => this.props.get_pwd(target.value)} placeholder="Digite aqui sua senha" type="password" />
            <Link className="to-costumer-base" to="/customers">
              <a>Fazer Login</a>
            </Link>
          </fieldset>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  Email: state.get_Email,
  Senha: state.get_Pwd
});

const mapDispatchToProps = (dispatch) =>({
 get_email:(get_Email) => dispatch(getEmail(get_Email)),
 get_pwd:(get_Pwd) => dispatch(getEmail(get_Pwd)) 
})

export default  connect(mapStateToProps, mapDispatchToProps)(Login)
