import { GET_NAME, GET_EMAIL, GET_AGE, GET_PWD } from "../actions/index";

const INITIAL_STATE = {
  Nome: "",
  Email: "",
  Idade: "",
  Senha: "",
};
export default function reducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case GET_NAME:
      return { ...state, Nome: action.get_Name };
    case GET_EMAIL:
      return { ...state, Email: action.get_Email };
    case GET_AGE:
      return { ...state, Idade: action.get_Age };
    case GET_PWD:
      return { ...state, Senha: action.get_Pwd };

    default:
      return state;
  }
}
