import React, { Component } from "react";
import "./Login.css";
import { Link, Redirect } from "react-router-dom";
import { getEmail, getPwd } from "../actions";
import { connect } from "react-redux";
import store from "../store";

class Login extends Component {
  console

  render() {
    return (
      <div className="background-login">
        <div className="login">
          <fieldset>
            <legend>Preencha os dados para Login</legend>
            <legend className="datas-to-login">Email</legend>
            <input
              onChange={({ target }) => this.props.get_email(target.value)}
              placeholder="Digite aqui seu email"
              type="text"
            />
            <legend className="datas-to-login">Senha</legend>
            <input
              onChange={({ target }) => this.props.get_pwd(target.value)}
              placeholder="Digite aqui sua senha"
              type="password"
            />
            {/* <Link className="to-costumer-base" to="/customers"> */}
            <a onClick={() => {
                const st = store.getState();
                const ret = Object.entries(st);
                let pwd;
                let mail;
                ret.forEach((element) => {
                  if (["Email"].includes(element[0])) {
                    mail = element[1];
                  }
                  if (["Senha"].includes(element[0])) {
                    pwd = element[1];
                  }
                });
                if ([""].includes(pwd, mail)) {
                  alert("nop");
                }
                <Redirect to="/customers"></Redirect>;
              } }>Fazer Login</a>

            {/* </Link> */}
          </fieldset>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  Email: state.get_Email,
  Senha: state.get_Pwd,
});

const mapDispatchToProps = (dispatch) => ({
  get_email: (get_Email) => dispatch(getEmail(get_Email)),
  get_pwd: (get_Pwd) => dispatch(getPwd(get_Pwd)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Login);
