import { GET_NAME_REGISTER, GET_AGE_REGISTER, GET_EMAIL_REGISTER, GET_CUSTOMER_REGISTERED } from "../actions/Register_Customers";

const INITIAL_STATE = {
  Nome_Registro: "",
  Idade_Registro: "",
  Email_Registro: "",
  Customer_Registered:{
    nome:'',
    idade:'',
    email:'',
  }
};
export function RegisterCustomersReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case GET_NAME_REGISTER:
      return { ...state, Nome_Registro: action.get_Name_Register, ...{Customer_Registered: {nome: action.get_Name_Register}}};
    case GET_EMAIL_REGISTER:
      return { ...state, Email_Registro: action.get_Email_Register, ...{Customer_Registered:{ email: action.get_Email_Register}} };
    case GET_AGE_REGISTER:
      return { ...state, Idade_Registro: action.get_Age_Register };
      // case GET_CUSTOMER_REGISTERED:
        // return { ...state, Idade_Registro: {
          // get_Name_Register,
          // get_Age_Register,
          // get_Email_Register
        // } };
    default:
      return state;
  }
}
